//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.6.12;

import "@openzeppelin/contracts/math/SafeMath.sol";
import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import '@openzeppelin/contracts/access/Ownable.sol';

contract FwarTicket is Ownable{
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    mapping(address => uint) tokenAddsExchangeRate;

    event buyTicketEvent(address indexed _caller, address _payToken, uint _amount);

    function isValidTokenAddress(address _tokenAddress) public view returns (bool) {
      require(_tokenAddress != address(0), "_token error");

      if(tokenAddsExchangeRate[_tokenAddress] > 0){
        return true;
      }
    }

    function setTokenAdd(address _tokenAddress, uint _rate) external onlyOwner {
      tokenAddsExchangeRate[_tokenAddress] = _rate;
    }

    function buyTicket(address _tokenAddress, uint _amount) external{ 
      require(_msgSender() == tx.origin, "FWarTicket: Can not call from contract");
      require(isValidTokenAddress(_tokenAddress), "Token address is not supported");

      uint exchangeRate = tokenAddsExchangeRate[_tokenAddress];
      uint total = exchangeRate.mul(_amount).mul(1e18);

      // transfer 
      IERC20(_tokenAddress).transferFrom(_msgSender(), address(this), total);
      emit buyTicketEvent(_msgSender(), _tokenAddress, _amount);
    }

    // function to allow admin to claim *other* ERC20 tokens sent to this contract (by mistake)
    function claimToken(address _tokenAddress, uint _amount) external onlyOwner {
      ERC20(_tokenAddress).transfer(_msgSender(), _amount);
    }
}
